describe('Conhecendo o Protractor', function() {

  var inputYourName = element(by.model('yourName'));

  beforeEach(function()
  {
    browser.get('/apps/ola/');
  });

  it('Checar o nome inicial', function() {
    expect(element(by.binding('yourName')).getText()).toBe("Hello !");
  });

it('Checar o nome inicial com entrada Lucas Zuão', function() {
  inputYourName.sendKeys('Lucas Zuããããããooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooo');
  expect(element(by.binding('yourName')).getText()).toBe("Hello Lucas Zuããããããooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooo!");
});

it('Checar limite de campo', function() {
  inputYourName.sendKeys('');
  expect(element(by.binding('yourName')).getText()).toBe("Hello !");
});
  
});